import React, { useState, useEffect } from 'react';
import { Layout as AntLayout, Menu, Card, Form, Input, Button, Modal } from 'antd';
import { MenuOutlined } from '@ant-design/icons';
import AppContent from './App';

const { Header, Content, Footer } = AntLayout;

const MyLayout = () => {
    const [aboutMeVisible, setAboutMeVisible] = useState(false);
    const [submenuVisible, setSubmenuVisible] = useState(false);
    const [cards, setCards] = useState([]);
    const [showAddCardForm, setShowAddCardForm] = useState(false);
    const [showInfoAboutCards, setShowInfoAboutCards] = useState(false);

    const toggleAboutMeModal = () => {
        setAboutMeVisible(!aboutMeVisible);
    };

    const toggleSubmenu = () => {
        setSubmenuVisible(!submenuVisible);
    };

    useEffect(() => {
        if (showInfoAboutCards) {
            window.location.hash = "#info-about-cards";
        }
    }, [showInfoAboutCards]);

    const addCard = (values) => {
        const newCard = {
            id: cards.length + 1,
            name: values.name,
            surname: values.surname,
            cardNumber: values.cardNumber,
            expiration: values.expiration,
            cvv: values.cvv
        };
        setCards([...cards, newCard]);
        setShowAddCardForm(false);
    };

    const handleInfoAboutCardsClick = () => {
        setShowInfoAboutCards(true);
    };

    return (
        <AntLayout style={{ minHeight: '100vh' }}>
            <Header>
                <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['0']} style={{ width: 'fit-content' }}>
                    <Menu.Item key="1" onClick={toggleAboutMeModal}>About me</Menu.Item>
                    <Menu.SubMenu
                        key="sub1"
                        title={<span onClick={toggleSubmenu}>Cards</span>}
                        popupClassName={submenuVisible ? "submenu-open" : ""}
                    >
                        <Menu.Item key="3" onClick={() => setShowAddCardForm(true)}>Add card</Menu.Item>
                        <Menu.Item key="4"><a id="info-about-cards" href="#info-about-cards" onClick={handleInfoAboutCardsClick}>Info about the card</a></Menu.Item>
                    </Menu.SubMenu>
                    <Menu.Item key="5"><a href="/">Contact Us</a></Menu.Item>
                </Menu>
            </Header>
            <Content style={{ padding: '0 50px' }}>
                <div className="site-layout-content">
                    {window.location.hash === "#info-about-cards" && showInfoAboutCards && (
                        <div>
                            {cards.map(card => (
                                <Card key={card.id} title={card.name + " " + card.surname}>
                                    <p><strong>Card Number:</strong> {card.cardNumber}</p>
                                    <p><strong>Expiration:</strong> {card.expiration}</p>
                                    <p><strong>CVV:</strong> {card.cvv}</p>
                                </Card>
                            ))}
                        </div>
                    )}
                    {showAddCardForm && (
                        <AddCardForm addCard={addCard} />
                    )}
                </div>
            </Content>
        </AntLayout>
    );
};

const AddCardForm = ({ addCard }) => {
    const [form] = Form.useForm();

    const onFinish = (values) => {
        addCard(values);
        form.resetFields();
    };

    return (
        <Form form={form} onFinish={onFinish}>
            <Form.Item name="name" rules={[{ required: true }]}>
                <Input placeholder="Name" />
            </Form.Item>
            <Form.Item name="surname" rules={[{ required: true }]}>
                <Input placeholder="Surname" />
            </Form.Item>
            <Form.Item name="cardNumber" rules={[{ required: true }]}>
                <Input placeholder="Card Number" />
            </Form.Item>
            <Form.Item name="expiration" rules={[{ required: true }]}>
                <Input placeholder="Expiration Date" />
            </Form.Item>
            <Form.Item name="cvv" rules={[{ required: true }]}>
                <Input placeholder="CVV" />
            </Form.Item>
            <Form.Item>
                <Button type="primary" htmlType="submit">
                    Add Card
                </Button>
            </Form.Item>
        </Form>
    );
};
