import React, { useState } from 'react';
import { Layout as AntLayout, Menu, Card, Form, Input, Button } from 'antd';

const { Header, Content, Footer, Sider } = AntLayout;

const MyLayout = () => {
    const [formData, setFormData] = useState({
        input1: '',
        input2: '',
        input3: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = () => {
        console.log('Formular trimis:', formData);
        alert('Formular trimis!');
    };

    const handleHomeClick = () => {
        alert("Bun venit la Grupa Noastră.\nNumele meu este Panfil Ion.\nSunt în grupa CR-221.");
    };

    const handleCardsClick = () => {
        alert('Contentul cardului 1\nContentul cardului 2\nContentul cardului 3');
    };

    return (
        <AntLayout style={{ minHeight: '100vh' }}>
            <Sider width={200} style={{ background: '#fff' }}>
                <Menu mode="inline" defaultSelectedKeys={['1']} style={{ height: '100%', borderRight: 0 }}>
                    <Menu.Item key="1" onClick={handleHomeClick}>Acasă</Menu.Item>
                    <Menu.Item key="2">Despre</Menu.Item>
                    <Menu.Item key="3">Contact</Menu.Item>
                    <Menu.Item key="4" onClick={handleCardsClick}>Carduri</Menu.Item>
                </Menu>
            </Sider>
            <AntLayout style={{ marginLeft: 200 }}>
                <Header style={{ background: '#fff', padding: 0 }}>Header</Header>
                <Content style={{ padding: '0 50px' }}>
                    <div className="site-layout-content">
                        <h2>Listă de Carduri</h2>
                        <Card title="Card 1" style={{ width: 300, margin: '0 16px 16px 0' }}></Card>
                        <Card title="Card 2" style={{ width: 300, margin: '0 16px 16px 0' }}></Card>
                        <Card title="Card 3" style={{ width: 300, margin: '0 16px 16px 0' }}></Card>
                        <Form onFinish={handleSubmit}>
                            <Form.Item label="Input 1">
                                <Input name="input1" value={formData.input1} onChange={handleChange} />
                            </Form.Item>
                            <Form.Item label="Input 2">
                                <Input name="input2" value={formData.input2} onChange={handleChange} />
                            </Form.Item>
                            <Form.Item label="Input 3">
                                <Input name="input3" value={formData.input3} onChange={handleChange} />
                            </Form.Item>
                            <Form.Item>
                                <Button type="primary" htmlType="submit">Trimite</Button>
                            </Form.Item>
                        </Form>
                    </div>
                </Content>
                <Footer style={{ textAlign: 'center' }}>
                    Contact: contact@exemplu.com | Telefon: +1234567890
                </Footer>
            </AntLayout>
        </AntLayout>
    );
};

export default MyLayout;
