import React, { useState } from 'react';
import { Layout as AntLayout, Menu, Drawer } from 'antd';
import { MenuOutlined } from '@ant-design/icons';

const { Header, Content, Footer } = AntLayout;

const MyLayout = () => {
    const [drawerVisible, setDrawerVisible] = useState(false);

    const showDrawer = () => {
        setDrawerVisible(true);
    };

    const onClose = () => {
        setDrawerVisible(false);
    };

    return (
        <AntLayout style={{ minHeight: '100vh' }}>
            <Header>
                <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['0']} style={{ width: 'fit-content' }}>
                    <Menu.Item key="1"><a href="/">Home</a></Menu.Item>
                    <Menu.Item key="2"><a href="/about">About Me</a></Menu.Item>
                    <Menu.Item key="6"><a href="/news">News</a></Menu.Item>
                    <Menu.Item key="7"><a href="/contact">Contact Us</a></Menu.Item>
                    <MenuOutlined onClick={showDrawer} style={{ marginLeft: 'auto', fontSize: '20px', cursor: 'pointer' }} />
                </Menu>
            </Header>
            <Drawer
                title="Menu"
                placement="left"
                closable={true}
                onClose={onClose}
                visible={drawerVisible}
            >
                <Menu theme="dark" mode="vertical" defaultSelectedKeys={['0']}>
                    <Menu.Item key="1"><a href="/">Home</a></Menu.Item>
                    <Menu.Item key="2"><a href="/about">About Me</a></Menu.Item>
                    <Menu.Item key="6"><a href="/news">News</a></Menu.Item>
                    <Menu.Item key="7"><a href="/contact">Contact Us</a></Menu.Item>
                </Menu>
            </Drawer>
            <Content style={{ padding: '0 50px' }}>
                <div className="site-layout-content">
                    {/* Restul con»õinutului */}
                </div>
            </Content>
            <Footer style={{ textAlign: 'center' }}>
                Contact: contact@exemplu.com | Telefon: +1234567890
            </Footer>
        </AntLayout>
    );
};

export default MyLayout;
