import React, { useState, useRef } from 'react';
import { Layout as AntLayout, Menu, Modal, Card, Form, Input, Button } from 'antd';
import { MenuOutlined } from '@ant-design/icons';
import AppContent from './App'; // Importăm componenta App

const { Header, Content, Footer } = AntLayout;

const MyLayout = () => {
    const [aboutMeVisible, setAboutMeVisible] = useState(false);
    const [submenuVisible, setSubmenuVisible] = useState(false);
    const [cards, setCards] = useState([]); // State pentru lista de carduri
    const [addCardModalVisible, setAddCardModalVisible] = useState(false); // Starea pentru vizibilitatea ferestrei modale de adăugare a cardului

    const toggleAboutMeModal = () => {
        setAboutMeVisible(!aboutMeVisible);
    };

    const toggleSubmenu = () => {
        setSubmenuVisible(!submenuVisible);
    };

    const addCard = (values) => {
        // Adăugăm un card nou în listă folosind valorile din formular
        const newCard = {
            id: cards.length + 1,
            name: values.name,
            surname: values.surname,
            cardNumber: values.cardNumber,
            expiration: values.expiration,
            cvv: values.cvv
        };
        setCards([...cards, newCard]);
        setAddCardModalVisible(false); // Închidem fereastra modală după adăugarea unui card
        // Deschidem secțiunea "Info about cards"
        document.getElementById("info-about-cards").click();
    };

    // Referință pentru fereastra modală
    const addCardModalRef = useRef(null);

    return (
        <AntLayout style={{ minHeight: '100vh' }}>
            <Header>
                <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['0']} style={{ width: 'fit-content' }}>
                    <Menu.Item key="1" onClick={toggleAboutMeModal}>About me</Menu.Item>
                    <Menu.SubMenu
                        key="sub1"
                        title={<span onClick={toggleSubmenu}>Cards</span>}
                        popupClassName={submenuVisible ? "submenu-open" : ""}
                    >
                        <Menu.Item key="3">
                            <Button type="text" onClick={() => setAddCardModalVisible(true)}>Add card</Button>
                        </Menu.Item>
                        <Menu.Item key="4"><a id="info-about-cards" href="#info-about-cards">Info about the card</a></Menu.Item>
                    </Menu.SubMenu>
                    <Menu.Item key="5"><a href="/">Contact Us</a></Menu.Item>
                </Menu>
            </Header>
            <Content style={{ padding: '0 50px' }}>
                <div className="site-layout-content">
                    {/* Afișarea listei de carduri */}
                    {cards.map(card => (
                        <Card key={card.id} title={card.name + " " + card.surname}>
                            <p><strong>Card Number:</strong> {card.cardNumber}</p>
                            <p><strong>Expiration:</strong> {card.expiration}</p>
                            <p><strong>CVV:</strong> {card.cvv}</p>
                        </Card>
                    ))}
                </div>
            </Content>
            <Footer style={{ textAlign: 'center' }}>
                Contact: contact@exemplu.com | Telefon: +1234567890
            </Footer>
            {/* Modal pentru adăugarea unui card */}
            <Modal
                title="Add Card"
                visible={addCardModalVisible}
                onCancel={() => setAddCardModalVisible(false)}
                footer={null}
                afterClose={() => {addCardModalRef.current.resetFields()}} // Resetează formularul după închiderea ferestrei modale
            >
                {/* Formular pentru adăugarea unui card */}
                <AddCardForm addCard={addCard} ref={addCardModalRef} />
            </Modal>
            {/* Modal pentru afișarea informațiilor despre "About me" */}
            <Modal
                title="About Me"
                visible={aboutMeVisible}
                onCancel={toggleAboutMeModal}
                footer={null}
            >
                <AppContent />
            </Modal>
        </AntLayout>
    );
};

// Componenta pentru formularul de adăugare a unui card
const AddCardForm = React.forwardRef(({ addCard }, ref) => {
    const [form] = Form.useForm();

    const onFinish = (values) => {
        addCard(values);
    };

    return (
        <Form ref={ref} form={form} onFinish={onFinish} layout="vertical">
            <Form.Item name="name" label="Nume" rules={[{ required: true, message: 'Vă rugăm să introduceți numele' }]}>
                <Input />
            </Form.Item>
            <Form.Item name="surname" label="Prenume" rules={[{ required: true, message: 'Vă rugăm să introduceți prenumele' }]}>
                <Input />
            </Form.Item>
            <Form.Item name="cardNumber" label="Număr card" rules={[{ required: true, message: 'Vă rugăm să introduceți numărul cardului' }]}>
                <Input />
            </Form.Item>
            <Form.Item name="expiration" label="Expirare" rules={[{ required: true, message: 'Vă rugăm să introduceți data de expirare' }]}>
                <Input />
            </Form.Item>
            <Form.Item name="cvv" label="CVV" rules={[{ required: true, message: 'Vă rugăm să introduceți codul CVV' }]}>
                <Input />
            </Form.Item>
            <Form.Item>
                <Button type="primary" htmlType="submit">Adaugă card</Button>
            </Form.Item>
        </Form>
    );
});

export default MyLayout;
