import React from 'react';
import { Layout as AntLayout, Menu } from 'antd';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom'; // Importă BrowserRouter și alte componente necesare pentru React Router

const { Header, Content, Footer } = AntLayout;

const menuItems = [
    { key: 'home', title: 'Home', link: '/' },
    { key: 'about', title: 'About', link: '/about' },
    { key: 'contact', title: 'Contact', link: '/contact' },
    // Alte elemente...
];

const Layout = () => {
    return (
        <Router> {/* Încadrează întregul layout în Router */}
            <AntLayout>
                <Header>
                    <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['home']}>
                        {menuItems.map(item => (
                            <Menu.Item key={item.key}>
                                <Link to={item.link}>{item.title}</Link>
                            </Menu.Item>
                        ))}
                    </Menu>
                </Header>
                <Content style={{ padding: '0 50px' }}>
                    <div className="site-layout-content">
                        <Switch> {/* Utilizează Switch pentru a afișa doar ruta corectă */}
                            <Route exact path="/" component={Home} />
                            <Route path="/about" component={About} />
                            <Route path="/contact" component={Contact} />
                            {/* Alte rute... */}
                        </Switch>
                    </div>
                </Content>
                <Footer style={{ textAlign: 'center' }}>
                    Contact: contact@exemplu.com | Telefon: +1234567890
                </Footer>
            </AntLayout>
        </Router>
    );
};

// Componente pentru rute
const Home = () => <h2>Acasă</h2>;
const About = () => <h2>Despre</h2>;
const Contact = () => <h2>Contact</h2>;
// Alte componente pentru alte rute...

export default Layout;
