import React, { useState } from 'react';
import { Layout as AntLayout, Menu, Card, Form, Input, Button } from 'antd';

const { Header, Content } = AntLayout;

const MyLayout = () => {
    const [aboutMeVisible, setAboutMeVisible] = useState(false);
    const [submenuVisible, setSubmenuVisible] = useState(false);
    const [cards, setCards] = useState([]);
    const [showAddCardForm, setShowAddCardForm] = useState(false);

    return (
        <AntLayout style={{ minHeight: '100vh' }}>
            <Header>
                <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['0']} style={{ width: 'fit-content' }}>
                    <Menu.Item key="1" onClick={() => setAboutMeVisible(!aboutMeVisible)}>About me</Menu.Item>
                    <Menu.SubMenu
                        key="sub1"
                        title={<span onClick={() => setSubmenuVisible(!submenuVisible)}>Cards</span>}
                        popupClassName={submenuVisible ? "submenu-open" : ""}
                    >
                        <Menu.Item key="3" onClick={() => setShowAddCardForm(true)}>Add card</Menu.Item>
                        <Menu.Item key="4">Info about the card</Menu.Item>
                    </Menu.SubMenu>
                    <Menu.Item key="5"><a href="/">Contact Us</a></Menu.Item>
                </Menu>
            </Header>
            <Content style={{ padding: '0 50px' }}>
                <div className="site-layout-content">
                    {/* Content here */}
                </div>
            </Content>
        </AntLayout>
    );
};

const AddCardForm = ({ addCard }) => {
    const [form] = Form.useForm();

    const onFinish = (values) => {
        addCard(values);
        form.resetFields();
    };

    return (
        <Form form={form} onFinish={onFinish}>
            <Form.Item name="name" rules={[{ required: true }]}>
                <Input placeholder="Name" />
            </Form.Item>
            <Form.Item name="surname" rules={[{ required: true }]}>
                <Input placeholder="Surname" />
            </Form.Item>
            <Form.Item name="cardNumber" rules={[{ required: true }]}>
                <Input placeholder="Card Number" />
            </Form.Item>
            <Form.Item name="expiration" rules={[{ required: true }]}>
                <Input placeholder="Expiration Date" />
            </Form.Item>
            <Form.Item name="cvv" rules={[{ required: true }]}>
                <Input placeholder="CVV" />
            </Form.Item>
            <Form.Item>
                <Button type="primary" htmlType="submit">
                    Add Card
                </Button>
            </Form.Item>
        </Form>
    );
};

export { MyLayout, AddCardForm };
