import React, { useState } from 'react';
import { Layout as AntLayout, Menu } from 'antd';
import { MenuOutlined } from '@ant-design/icons';
import AppContent from './App'; // Importăm componenta App

const { Header, Content, Footer } = AntLayout;

const MyLayout = () => {
    const [submenuVisible, setSubmenuVisible] = useState(false);

    const toggleSubmenu = () => {
        setSubmenuVisible(!submenuVisible);
    };

    // Funcția care returnează conținutul corespunzător butonului "About me"
    const renderAboutMeContent = () => (
        <div className="App">
            <header className="App-header">
                <h1>Bun venit la Grupa Noastră</h1>
                <p>Numele meu este [Panfil] [Ion].</p>
                <p>Sunt în grupa [CR-221].</p>
            </header>
        </div>
    );

    return (
        <AntLayout style={{ minHeight: '100vh' }}>
            <Header>
                <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['0']} style={{ width: 'fit-content' }}>
                    <Menu.SubMenu
                        key="sub1"
                        title={<span onClick={toggleSubmenu}>About me</span>}
                        popupClassName={submenuVisible ? "submenu-open" : ""}
                    >
                        <Menu.Item key="1">{/* Înlocuim acest element cu conținutul */}
                            {renderAboutMeContent()}
                        </Menu.Item>
                    </Menu.SubMenu>
                </Menu>
            </Header>
            <Content style={{ padding: '0 50px' }}>
                <div className="site-layout-content">
                    {/* Restul conținutului */}
                </div>
            </Content>
            <Footer style={{ textAlign: 'center' }}>
                Contact: contact@exemplu.com | Telefon: +1234567890
            </Footer>
        </AntLayout>
    );
};

export default MyLayout;
