import React, { useState } from 'react';
import { Layout as AntLayout, Menu, Card, Form, Input, Button } from 'antd';

const { Header, Content } = AntLayout;

const MyLayout = () => {
    const [aboutMeVisible, setAboutMeVisible] = useState(false);
    const [submenuVisible, setSubmenuVisible] = useState(false);
    const [cards, setCards] = useState([]);
    const [showAddCardForm, setShowAddCardForm] = useState(false);

    const toggleAboutMeModal = () => {
        setAboutMeVisible(!aboutMeVisible);
    };

    const toggleSubmenu = () => {
        setSubmenuVisible(!submenuVisible);
    };

    const addCard = (values) => {
        const newCard = {
            id: cards.length + 1,
            name: values.name,
            surname: values.surname,
            cardNumber: values.cardNumber,
            expirationMonth: values.expirationMonth,
            expirationYear: values.expirationYear,
            cvv: values.cvv
        };
        setCards([...cards, newCard]);
        setShowAddCardForm(false);
    };

    return (
        <AntLayout style={{ minHeight: '100vh' }}>
            <Header>
                <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['0']} style={{ width: 'fit-content' }}>
                    <Menu.Item key="1" onClick={toggleAboutMeModal}>About me</Menu.Item>
                    <Menu.SubMenu
                        key="sub1"
                        title={<span onClick={toggleSubmenu}>Cards</span>}
                        popupClassName={submenuVisible ? "submenu-open" : ""}
                    >
                        <Menu.Item key="3" onClick={() => setShowAddCardForm(true)}>Add card</Menu.Item>
                    </Menu.SubMenu>
                    <Menu.Item key="5"><a href="/">Contact Us</a></Menu.Item>
                </Menu>
            </Header>
            <Content style={{ padding: '0 50px' }}>
                <div className="site-layout-content" style={{ display: 'flex', flexDirection: 'row', flexWrap: 'wrap' }}>
                    {showAddCardForm && (
                        <AddCardForm addCard={addCard} />
                    )}
                    {cards.map(card => (
                        <Card key={card.id} title={card.name + " " + card.surname} style={{ width: '300px', margin: '16px' }}>
                            <p><strong>Card Number:</strong> {card.cardNumber}</p>
                            <p><strong>Expiration:</strong> {card.expirationMonth}/{card.expirationYear}</p>
                            <p><strong>CVV:</strong> {card.cvv}</p>
                        </Card>
                    ))}
                </div>
            </Content>
        </AntLayout>
    );
};

const AddCardForm = ({ addCard }) => {
    const [form] = Form.useForm();

    const onFinish = (values) => {
        addCard(values);
        form.resetFields();
    };

    const validateName = (_, value) => {
        const regex = /^[a-zA-Z]+$/;
        if (!regex.test(value)) {
            return Promise.reject(new Error('Please enter only letters for name'));
        }
        return Promise.resolve();
    };

    const validateSurname = (_, value) => {
        const regex = /^[a-zA-Z]+$/;
        if (!regex.test(value)) {
            return Promise.reject(new Error('Please enter only letters for surname'));
        }
        return Promise.resolve();
    };

    const validateCardNumber = (_, value) => {
        const regex = /^\d+$/;
        if (!regex.test(value)) {
            return Promise.reject(new Error('Please enter only numbers for card number'));
        }
        return Promise.resolve();
    };

    const validateExpirationMonth = (_, value) => {
        if (value < 1 || value > 12) {
            return Promise.reject(new Error('Please enter a valid month (1-12)'));
        }
        return Promise.resolve();
    };

    const validateExpirationYear = (_, value) => {
        const regex = /^\d{2}$/;
        if (!regex.test(value)) {
            return Promise.reject(new Error('Please enter a valid 2-digit year'));
        }
        return Promise.resolve();
    };

    const validateCVV = (_, value) => {
        const regex = /^\d{3}$/;
        if (!regex.test(value)) {
            return Promise.reject(new Error('Please enter a valid 3-digit CVV'));
        }
        return Promise.resolve();
    };

    return (
        <Form form={form} onFinish={onFinish}>
            <Form.Item name="name" rules={[{ required: true, validator: validateName }]}>
                <Input placeholder="Name" />
            </Form.Item>
            <Form.Item name="surname" rules={[{ required: true, validator: validateSurname }]}>
                <Input placeholder="Surname" />
            </Form.Item>
            <Form.Item name="cardNumber" rules={[{ required: true, validator: validateCardNumber }]}>
                <Input placeholder="Card Number" />
            </Form.Item>
            <Form.Item name="expirationMonth" rules={[{ required: true, validator: validateExpirationMonth }]}>
                <Input placeholder="Expiration Month" />
            </Form.Item>
            <Form.Item name="expirationYear" rules={[{ required: true, validator: validateExpirationYear }]}>
                <Input placeholder="Expiration Year" />
            </Form.Item>
            <Form.Item name="cvv" rules={[{ required: true, validator: validateCVV }]}>
                <Input placeholder="CVV" />
            </Form.Item>
            <Form.Item>
                <Button type="primary" htmlType="submit">
                    Add Card
                </Button>
            </Form.Item>
        </Form>
    );
};

export default MyLayout;
