import React, { useState } from 'react';
import { Layout as AntLayout, Menu, Button } from 'antd';

const { Header, Content, Footer } = AntLayout;

const MyLayout = () => {
    const [menuVisible, setMenuVisible] = useState(false);

    const toggleMenu = () => {
        setMenuVisible(!menuVisible);
    };

    return (
        <AntLayout style={{ minHeight: '100vh' }}>
            <Header>
                <Button icon={menuVisible ? 'menu-fold' : 'menu-unfold'} onClick={toggleMenu} />
                <Menu theme="dark" mode="horizontal" style={{ display: menuVisible ? 'block' : 'none' }}>
                    <Menu.Item key="1">Acasă</Menu.Item>
                    <Menu.Item key="2">Despre</Menu.Item>
                    <Menu.Item key="3">Contact</Menu.Item>
                </Menu>
            </Header>
            <Content style={{ padding: '0 50px' }}>
                <div className="site-layout-content">
                    {/* Restul conținutului */}
                </div>
            </Content>
            <Footer style={{ textAlign: 'center' }}>
                Contact: contact@exemplu.com | Telefon: +1234567890
            </Footer>
        </AntLayout>
    );
};

export default MyLayout;
