import React, { useState } from 'react';
import { Layout as AntLayout, Menu, Card, Form, Input, Button } from 'antd';
const { Header, Content } = AntLayout;

const MyLayout = () => {
    // ... existing state variables ...
    const [aboutMeVisible, setAboutMeVisible] = useState(false);
    const [submenuVisible, setSubmenuVisible] = useState(false);
    const [cards, setCards] = useState([]);
    const [showAddCardForm, setShowAddCardForm] = useState(false);
    // Add a new state variable for card info visibility
    const [cardInfoVisible, setCardInfoVisible] = useState(false);

    // ... existing functions ...

    // Add a new function to toggle card info visibility
    const toggleCardInfo = () => {
        setCardInfoVisible(!cardInfoVisible);
    };

    return (
        <AntLayout style={{ minHeight: '100vh' }}>
            <Header>
                <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['0']} style={{ width: 'fit-content' }}>
                    {/* ... existing menu items ... */}
                    <Menu.Item key="4" onClick={toggleCardInfo}>Info about the card</Menu.Item>
                    {/* ... existing menu items ... */}
                </Menu>
            </Header>
            <Content style={{ padding: '0 50px' }}>
                <div className="site-layout-content">
                    {/* Only render the card info if cardInfoVisible is true */}
                    {cardInfoVisible && (
                        <div id="info-about-cards">
                            {cards.map(card => (
                                <Card key={card.id} title={card.name + " " + card.surname}>
                                    <p><strong>Card Number:</strong> {card.cardNumber}</p>
                                    <p><strong>Expiration:</strong> {card.expiration}</p>
                                    <p><strong>CVV:</strong> {card.cvv}</p>
                                </Card>
                            ))}
                        </div>
                    )}
                    {showAddCardForm && (
                        <AddCardForm addCard={addCard} />
                    )}
                </div>
            </Content>
        </AntLayout>
    );
};

// ... existing AddCardForm component ...

export default MyLayout;
