import React, { useState } from 'react';
import { Layout as AntLayout, Menu, Modal, Card } from 'antd';
import { MenuOutlined } from '@ant-design/icons';
import AppContent from './App'; // Importăm componenta App

const { Header, Content, Footer } = AntLayout;

const MyLayout = () => {
    const [aboutMeVisible, setAboutMeVisible] = useState(false);
    const [submenuVisible, setSubmenuVisible] = useState(false);
    const [cards, setCards] = useState([]); // State pentru lista de carduri

    const toggleAboutMeModal = () => {
        setAboutMeVisible(!aboutMeVisible);
    };

    const toggleSubmenu = () => {
        setSubmenuVisible(!submenuVisible);
    };

    const addCard = () => {
        // Simulăm adăugarea unui card nou în listă
        const newCard = {
            id: cards.length + 1,
            title: `Card ${cards.length + 1}`,
            content: `Content for Card ${cards.length + 1}`
        };
        setCards([...cards, newCard]);
    };

    return (
        <AntLayout style={{ minHeight: '100vh' }}>
            <Header>
                <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['0']} style={{ width: 'fit-content' }}>
                    <Menu.Item key="1" onClick={toggleAboutMeModal}>About me</Menu.Item>
                    <Menu.SubMenu
                        key="sub1"
                        title={<span onClick={toggleSubmenu}>Cards</span>}
                        popupClassName={submenuVisible ? "submenu-open" : ""}
                    >
                        <Menu.Item key="3" onClick={addCard}>Add card</Menu.Item>
                        <Menu.Item key="4"><a href="/">Info about the card</a></Menu.Item>
                    </Menu.SubMenu>
                    <Menu.Item key="5"><a href="/">Contact Us</a></Menu.Item>
                </Menu>
            </Header>
            <Content style={{ padding: '0 50px' }}>
                <div className="site-layout-content">
                    {/* Afișarea listei de carduri */}
                    {cards.map(card => (
                        <Card key={card.id} title={card.title}>
                            {card.content}
                        </Card>
                    ))}
                </div>
            </Content>
            <Footer style={{ textAlign: 'center' }}>
                Contact: contact@exemplu.com | Telefon: +1234567890
            </Footer>
            {/* Modal pentru afișarea informațiilor despre "About me" */}
            <Modal
                title="About Me"
                visible={aboutMeVisible}
                onCancel={toggleAboutMeModal}
                footer={null}
            >
                <AppContent />
            </Modal>
        </AntLayout>
    );
};

export default MyLayout;
