// MYLayout.js

// Create the navigation menu toggle
const menuToggle = document.createElement('div');
menuToggle.id = 'menuToggle';

// Create the checkbox input
const checkboxInput = document.createElement('input');
checkboxInput.type = 'checkbox';
menuToggle.appendChild(checkboxInput);

// Create the hamburger spans
for (let i = 0; i < 3; i++) {
    const span = document.createElement('span');
    menuToggle.appendChild(span);
}

// Create the menu ul
const menu = document.createElement('ul');
menu.id = 'menu';
menu.innerHTML = `
  <a href="#"><li>Home</li></a>
  <a href="#"><li>About</li></a>
  <a href="#"><li>Info</li></a>
  <a href="#"><li>Contact</li></a>
  <a href="https://erikterwan.com/" target="_blank"><li>Show me more</li></a>
`;

// Append the menu to the menuToggle
menuToggle.appendChild(menu);

// Append the menuToggle to the body
document.body.appendChild(menuToggle);

// Add event listener to toggle menu visibility
checkboxInput.addEventListener('change', function() {
    const menu = document.getElementById('menu');
    if (checkboxInput.checked) {
        menu.style.transform = 'none';
    } else {
        menu.style.transform = 'translate(-100%, 0)';
    }
});
